                    control={control}
                    rules={{ required: true }}
                    name="clientId"
                    render={({ field, value, ref }) => (
                      <Select
                        inputRef={ref}
                        className="react-select col-lg-9 col-sm-12"
                        classNamePrefix="addl-class"
                        options={options}
                        value={options.find(c => c.value === value)}
                        onChange={val => field.onChange(val.value)}
                      />
