              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Service</Label>
                  <Controller
                    control={control}
                    name="serviceId"
                    rules={{ required: true }}
                    render={({ field, value, ref }) => (
                      <Select
                        {...register("serviceId")}
                        inputRef={ref}
