// ** React Imports
import { Fragment, useState, useEffect, useRef } from 'react'

// ** Custom Components
import AddActions from './AddActions'
import Repeater from '@components/repeater'

// ** Third Party Components
import axios from 'axios'
import Flatpickr from 'react-flatpickr'
import BillingAddress from './BillingAddress'
import { SlideDown } from 'react-slidedown'
import { X, Plus, Hash } from 'react-feather'
import Select, { components } from 'react-select'
import { useForm, useFieldArray } from "react-hook-form"

const countryOptions = [
  { value: 'uk', label: 'UK' },
  { value: 'usa', label: 'USA' },
  { value: 'france', label: 'France' },
  { value: 'russia', label: 'Russia' },
  { value: 'canada', label: 'Canada' }
]

// ** Reactstrap Imports
import { selectThemeColors } from '@utils'
import { Row, Col, Card, Form, Label, Button, CardBody, CardText, InputGroup, InputGroupText, Input } from 'reactstrap'

// ** Styles
import 'react-slidedown/lib/slidedown.css'
import '@styles/react/libs/react-select/_react-select.scss'
import '@styles/react/libs/flatpickr/flatpickr.scss'
import '@styles/base/pages/app-invoice.scss'

// ** Deletes form

const AddCard = () => {
  // ** States
  const [value, setValue] = useState({})
  const inputRef = useRef(null)
  const [setOpen] = useState(false)
  // const [clients, setClients] = useState(null)
  // const [selected, setSelected] = useState(null)
  const [picker, setPicker] = useState(new Date())
  const [options, setOptions] = useState([
    {
      value: 'add-new',
      label: 'Add New Customer',
      type: 'button',
      color: 'flat-success'
    }
  ])
  const { register, handleSubmit, control, formState: { errors } } = useForm({
    defaultValues: {
      clientType: '1',
      contactPersonName: '',
      businessName: '',
      itemArray: []
    }
  })

  const { fields, append, remove } = useFieldArray({ name: 'invoice_items', control })
  const onSubmit = data => console.log(data)

  const addItem = (() => {
    console.log('added')
    append({ itemId: '', sacCode: '', price: 0, taxGroupId: '', subTotal: 0, taxPrice: 0 })
  })

  const removeItem = ((val) => {
    remove(val)
  })

  useEffect(() => {
    addItem()
  }, [])


  useEffect(() => {
    // ** Get Clients
    axios.get('/api/invoice/clients').then(response => {
      const arr = options
      response.data.map(item => arr.push({ value: item.name, label: item.name }))
      setOptions([...arr])
      // setClients(response.data)
    })


  }, [])

  const changeHandler = (event) => {
    console.log(event.target.files)
  }

  // const handleSubmission = () => {
  //   const formData = new FormData()

  //     // Update the formData object
  //     formData.append(
  //       "myFile",
  //       selectedFile,
  //       selectedFile.name
  //     )

  //     // Details of the uploaded file
  //     console.log(selectedFile)
  // }

  // ** Custom Options Component
  const OptionComponent = ({ data, ...props }) => {
    if (data.type === 'button') {
      return (
        <Button className='text-start rounded-0 px-50' color={data.color} block onClick={() => setOpen(true)}>
          <Plus className='font-medium-1 me-50' />
          <span className='align-middle'>{data.label}</span>
        </Button>
      )
    } else {
      return <components.Option {...props}> {data.label} </components.Option>
    }
  }

  return (

    <form onSubmit={handleSubmit(onSubmit)}>
      <Card className='invoice-preview-card'>
        {/* Header */}
        <CardBody className='pb-2 px-2'>
          <Row>
            <div className='col-lg-6 col-sm-12'>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Client</Label>
                  <Select
                    id='label'
                    value={value}
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("clientId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Service</Label>
                  <Select
                    id='label'
                    value={value}
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("serviceId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <Col>
                  <div className='d-lg-flex'>
                    <Label className='col-lg-3 col-sm-12' >Description</Label>
                    <textarea className='form-control' size="md" ></textarea>
                  </div>
                </Col>
              </Row>
              <Row className='my-2'>
                <Col>
                  <div className='d-lg-flex'>
                    <input type='file' className='hidden' multiple onChange={changeHandler} ref={inputRef} />
                    <Button type='button' outline color='primary' onClick={() => inputRef.current.click()}>
                      <Plus size={14} className='me-25'></Plus> Add Attachment</Button>
                  </div>
                </Col>
              </Row>
            </div>
            <div className='col-lg-6 col-sm-12'>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Assignee</Label>
                  <Select
                    isMulti
                    id='label'
                    value={value}
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("assignee")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Reviewer</Label>
                  <Select
                    id='label'
                    value={value}
                    isMulti
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("reviewer")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <Col>
                  <div className='d-lg-flex'>
                    <Label className='col-lg-3 col-sm-12' >Start Date</Label>
                    <Flatpickr
                      value={picker}
                      onChange={date => setPicker(date)}
                      className='form-control due-date-picker'
                      {...register("startDate")}
                    />
                  </div>
                </Col>
              </Row>
              <Row className='my-2'>
                <Col>
                  <div className='d-lg-flex'>
                    <Label className='col-lg-3 col-sm-12' >End Date</Label>
                    <Flatpickr
                      value={picker}
                      onChange={date => setPicker(date)}
                      className='form-control due-date-picker'
                      {...register("EndDate")}
                    />
                  </div>
                </Col>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Priority</Label>
                  <Select
                    id='label'
                    value={value}
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("priority")}
                  />
                </div>
              </Row>
            </div>
          </Row>
        </CardBody>
        <hr className='invoice-spacing' />
        <Row className='px-1'>
          <div className='form-check form-check-primary mx-2'>
            <Input type='checkbox' id='client_type_2' name='clientType' value='1' {...register("invoiceFlag")} />
            <Label className='form-check-label' for='client_type_2'>
              Create Proposal for this Task
            </Label>
          </div>
        </Row>
        <CardBody className='invoice-padding invoice-product-details'>
          {fields.map((item, i) => (
            <div key={i} className='repeater-wrapper'>
              <Row>
                <Col className='d-lg-flex product-details-border position-relative pe-0' sm='12'>
                  <Row className='w-100 pe-lg-0 pe-1 py-2'>
                    <Col className='mb-lg-0 mb-2 mt-lg-0 mt-2 col-lg-3 col-sm-12'>
                      <CardText className='col-title mb-md-50 mb-0'>Item</CardText>
                      <Select
                        id='label'
                        value={value}
                        theme={selectThemeColors}
                        className={`react-select ${errors.invoice_items?.[i]?.taxGroupId ? 'is-invalid' : ''}`}
                        classNamePrefix='Select Tax Rate'
                        options={countryOptions}
                        isClearable={false}
                        {...register(`invoice_items.${i}.taxGroupId`, { required: true })} />
                    </Col>
                    <Col className='my-lg-0 my-2 col-lg-3 col-sm-12'>
                      <CardText className='col-title mb-md-2 mb-0'>SAC Code</CardText>
                      <input type='email' {...register(`invoice_items.${i}.email`, { required: true })} className={`form-control ${errors.invoice_items?.[i]?.email ? 'is-invalid' : ''}`} />
                      <div className="invalid-feedback">{errors.invoice_items?.[i]?.email?.message}</div>
                    </Col>
                    <Col className='my-lg-0 my-2' lg='2' sm='12'>
                      <CardText className='col-title mb-md-2 mb-0'>Price</CardText>
                      <input className='form-control' type='number' placeholder='' {...register(`invoice_items.${i}.contactNo`)} />
                    </Col>
                    <Col className='my-lg-0 mt-2' lg='2' sm='12'>
                      <CardText className='col-title mb-md-50 mb-0'>Tax Rate</CardText>
                      <Select
                        id='label'
                        value={value}
                        theme={selectThemeColors}
                        className={`react-select ${errors.invoice_items?.[i]?.taxGroupId ? 'is-invalid' : ''}`}
                        classNamePrefix='Select Tax Rate'
                        options={countryOptions}
                        isClearable={false}
                        {...register(`invoice_items.${i}.taxGroupId`, { required: true })} />
                    </Col>
                    <Col className='my-lg-0 mt-2' lg='1' sm='12'>
                      <CardText className='col-title mb-md-50 mb-0'>Amount</CardText>

                    </Col>
                  </Row>
                  <div className='d-lg-flex justify-content-center border-start invoice-product-actions py-50 px-25'>
                    <X size={18} className='cursor-pointer' onClick={() => removeItem(i)} />
                  </div>
                </Col>
              </Row>
            </div>
          ))}
          <Row className='mt-1'>
            <Col sm='12' className='px-0'>
              <Button color='primary' size='sm' className='btn-add-new' onClick={() => addItem()}>
                <Plus size={14} className='me-25'></Plus> <span className='align-middle'>Add Item</span>
              </Button>
            </Col>
          </Row>
        </CardBody>
      </Card>
      <Card>
        <CardBody>
          <div className='modal-footer border-0'>
            <Button color='warning' outline>
              Cancel
            </Button>
            <Button color='primary' type="submit" >
              Save
            </Button>
          </div>
        </CardBody>
      </Card>
    </form >
  )
}

export default AddCard
