// ** React Imports
import { Fragment, useState, useEffect } from 'react'

// ** Custom Components
import AddActions from './AddActions'
import Repeater from '@components/repeater'

// ** Third Party Components
import axios from 'axios'
import Flatpickr from 'react-flatpickr'
import BillingAddress from './BillingAddress'
import { SlideDown } from 'react-slidedown'
import { X, Plus, Hash } from 'react-feather'
import Select, { components } from 'react-select'
import { useForm, useFieldArray } from "react-hook-form"

const countryOptions = [
  { value: 'uk', label: 'UK' },
  { value: 'usa', label: 'USA' },
  { value: 'france', label: 'France' },
  { value: 'russia', label: 'Russia' },
  { value: 'canada', label: 'Canada' }
]

// ** Reactstrap Imports
import { selectThemeColors } from '@utils'
import { Row, Col, Card, Form, Label, Button, CardBody, CardText, InputGroup, InputGroupText, Input } from 'reactstrap'

// ** Styles
import 'react-slidedown/lib/slidedown.css'
import '@styles/react/libs/react-select/_react-select.scss'
import '@styles/react/libs/flatpickr/flatpickr.scss'
import '@styles/base/pages/app-invoice.scss'

// ** Deletes form
const deleteForm = e => {
  e.preventDefault()
  e.target.closest('.repeater-wrapper').remove()
}

const AddCard = () => {
  // ** States
  const [count] = useState(1)
  //const [value, setValue] = useState({})
  const [setOpen] = useState(false)
  // const [clients, setClients] = useState(null)
  // const [selected, setSelected] = useState(null)
  const [picker, setPicker] = useState(new Date())
  // const [invoiceNumber, setInvoiceNumber] = useState(false)
  //const [dueDatepicker, setDueDatePicker] = useState(new Date())
  const [options, setOptions] = useState([
    {
      value: 'add-new',
      label: 'Add New Customer',
      type: 'button',
      color: 'flat-success'
    }
  ])
  const { register, handleSubmit, watch, control } = useForm({
    defaultValues: {
      clientType: '1',
      contactPersonName: '',
      businessName: '',
      itemArray: []
    }
  })

  const { fields, append, remove } = useFieldArray({ name: 'contact_info', control })
  const onSubmit = data => console.log(data)
  const clientType = watch('clientType')
  console.log(clientType)
  useEffect(() => {
    // update field array when ticket number changed
    const newVal = parseInt(count || 0)
    const oldVal = fields.length
    if (newVal > oldVal) {
      // append tickets to field array
      for (let i = oldVal; i < newVal; i++) {
        append({ firstName: '', email: '', designation: '', contactNo: '', isPrimary: '' })
      }
    } else {
      // remove tickets from field array
      for (let i = oldVal; i > newVal; i--) {
        remove(i - 1)
      }
    }
  })

  useEffect(() => {
    // ** Get Clients
    axios.get('/api/invoice/clients').then(response => {
      const arr = options
      response.data.map(item => arr.push({ value: item.name, label: item.name }))
      setOptions([...arr])
      // setClients(response.data)
    })


  }, [])

  // ** Deletes form
  // const deleteForm = e => {
  //   e.preventDefault()
  //   e.target.closest('.repeater-wrapper').remove()
  // }


  // ** Custom Options Component
  const OptionComponent = ({ data, ...props }) => {
    if (data.type === 'button') {
      return (
        <Button className='text-start rounded-0 px-50' color={data.color} block onClick={() => setOpen(true)}>
          <Plus className='font-medium-1 me-50' />
          <span className='align-middle'>{data.label}</span>
        </Button>
      )
    } else {
      return <components.Option {...props}> {data.label} </components.Option>
    }
  }

  return (

    <form onSubmit={handleSubmit(onSubmit)}>
      <Card className='invoice-preview-card'>
        {/* Header */}
        <CardBody className='pb-2 px-2'>
          <Row>
            <Col className='col-lg-6 col-sm-12'>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Client</Label>
                  <Select
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("taxGroupId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Service</Label>
                  <Select
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("taxGroupId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Description</Label>
                  <input className='form-control col-lg-9 col-sm-12' type='text' />
                </div>
              </Row>
            </Col>
            <Col className='col-lg-6 col-sm-12'>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Assignee</Label>
                  <Select
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("taxGroupId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Reviewer</Label>
                  <Select
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("taxGroupId")}
                  />
                </div>
              </Row>
              <Row className='my-2'>
                <Label className='col-lg-3 col-sm-12' >Start Date</Label>
                <Flatpickr
                  value={picker}
                  onChange={date => setPicker(date)}
                  className='form-control due-date-picker col-lg-9 col-sm-12'
                />
              </Row>
              <Row className='my-2'>
                <Label className='col-lg-3 col-sm-12' >End Date</Label>
                <Flatpickr
                  value={picker}
                  onChange={date => setPicker(date)}
                  className='form-control due-date-picker col-lg-9 col-sm-12'
                />
              </Row>
              <Row className='my-2'>
                <div className='d-lg-flex'>
                  <Label className='col-lg-3 col-sm-12' >Priority</Label>
                  <Select
                    theme={selectThemeColors}
                    className={`react-select col-lg-9 col-sm-12`}
                    classNamePrefix='Select Tax Rate'
                    options={countryOptions}
                    isClearable={false}
                    {...register("taxGroupId")}
                  />
                </div>
              </Row>
            </Col>
          </Row>
        </CardBody>
      </Card>
      <hr className='invoice-spacing' />
      <Card>
        <CardBody className='invoice-padding invoice-product-details'>
          <Row>
            <div className='form-check form-check-primary mx-2'>
              <Input type='checkbox' id='client_type_2' name='clientType' value='1' {...register("clientType")} />
              <Label className='form-check-label' for='client_type_2'>
                Create Proposal for this Task
              </Label>
            </div>
          </Row>
          <Repeater count={count}>
            {i => {
              const Tag = i === 0 ? 'div' : SlideDown
              return (
                <Tag key={i} className='repeater-wrapper'>
                  <Row>
                    <Col className='d-flex product-details-border position-relative pe-0' sm='12'>
                      <Row className='w-100 pe-lg-0 pe-1 py-2'>
                        <Col className='mb-lg-0 mb-2 mt-lg-0 mt-2' lg='5' sm='12'>
                          <CardText className='col-title mb-md-50 mb-0'>Item</CardText>
                          <Input type='select' className='item-details'>
                            <option>App Design</option>
                            <option>App Customization</option>
                            <option>ABC Template</option>
                            <option>App Development</option>
                          </Input>
                          <Input className='mt-2' type='textarea' rows='1' defaultValue='Customization & Bug Fixes' />
                        </Col>
                        <Col className='my-lg-0 my-2' lg='3' sm='12'>
                          <CardText className='col-title mb-md-2 mb-0'>Cost</CardText>
                          <Input type='number' defaultValue='24' placeholder='24' />
                          <div className='mt-2'>
                            <span>Discount:</span> <span>0%</span>
                          </div>
                        </Col>
                        <Col className='my-lg-0 my-2' lg='2' sm='12'>
                          <CardText className='col-title mb-md-2 mb-0'>Qty</CardText>
                          <Input type='number' defaultValue='1' placeholder='1' />
                        </Col>
                        <Col className='my-lg-0 mt-2' lg='2' sm='12'>
                          <CardText className='col-title mb-md-50 mb-0'>Price</CardText>
                          <CardText className='mb-0'>$24.00</CardText>
                        </Col>
                      </Row>
                      <div className='d-flex justify-content-center border-start invoice-product-actions py-50 px-25'>
                        <X size={18} className='cursor-pointer' onClick={deleteForm} />
                      </div>
                    </Col>
                  </Row>
                </Tag>
              )
            }}
          </Repeater>
        </CardBody>
      </Card>
      <Card>
        <CardBody>
          <div className='modal-footer border-0'>
            <Button color='warning' outline>
              Cancel
            </Button>
            <Button color='primary' type="submit" >
              Save
            </Button>
          </div>
        </CardBody>
      </Card>
    </form >
  )
}

export default AddCard
