// ** React Imports
import { Fragment, useState, useEffect } from 'react'

// ** Custom Components
import AddActions from './AddActions'
import Repeater from '@components/repeater'

// ** Third Party Components
import axios from 'axios'
import Flatpickr from 'react-flatpickr'
import { SlideDown } from 'react-slidedown'
import { X, Plus, Hash } from 'react-feather'
import Select, { components } from 'react-select'
import { useForm, useFieldArray } from "react-hook-form"

const countryOptions = [
  { value: 'uk', label: 'UK' },
  { value: 'usa', label: 'USA' },
  { value: 'france', label: 'France' },
  { value: 'russia', label: 'Russia' },
  { value: 'canada', label: 'Canada' }
]

// ** Reactstrap Imports
import { selectThemeColors } from '@utils'
import { Row, Col, Card, Form, Label, Button, CardBody, CardText, InputGroup, InputGroupText, Input } from 'reactstrap'

// ** Styles
import 'react-slidedown/lib/slidedown.css'
import '@styles/react/libs/react-select/_react-select.scss'
import '@styles/react/libs/flatpickr/flatpickr.scss'
import '@styles/base/pages/app-invoice.scss'

const AddCard = () => {
  // ** States
  const [count] = useState(1)
  //const [value, setValue] = useState({})
  const [setOpen] = useState(false)
  // const [clients, setClients] = useState(null)
  // const [selected, setSelected] = useState(null)
  // const [picker, setPicker] = useState(new Date())
  // const [invoiceNumber, setInvoiceNumber] = useState(false)
  // const [dueDatepicker, setDueDatePicker] = useState(new Date())
  const [options, setOptions] = useState([
    {
      value: 'add-new',
      label: 'Add New Customer',
      type: 'button',
      color: 'flat-success'
    }
  ])
  const { register, handleSubmit, control, formState: { errors } } = useForm({
    defaultValues: {
      category: '',
      name: '',
      professionalFee: '',
      sacCode: '',
      taxGroupId: '',
      taxExemptionId: '',
      description: ''
    }
  })


  const onSubmit = data => console.log(data)

  useEffect(() => {
    // ** Get Clients
    axios.get('/api/invoice/clients').then(response => {
      const arr = options
      response.data.map(item => arr.push({ value: item.name, label: item.name }))
      setOptions([...arr])
      // setClients(response.data)
    })


  }, [])

  return (

    <form onSubmit={handleSubmit(onSubmit)}>
      <Card className='invoice-preview-card'>
        {/* Header */}
        <CardBody className='pb-0'>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12' >Category</Label>
                <Select
                  theme={selectThemeColors}
                  classNamePrefix='Select Category'
                  options={countryOptions}
                  isClearable={false}
                  className={`react-select col-lg-9 col-sm-12 ${errors.category ? 'is-invalid' : ''}`}
                  {...register("category", { required: "Please Select Category." })}
                  onChange={data => {
                    console.log(data)
                  }}
                />
              </div>
            </Col>
          </Row>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12' >Name</Label>
                <input className={`form-control ${errors.name ? 'is-invalid' : ''}`} size="md" type='text' {...register("name", { required: "Please Enter Service Names" })} />
              </div>
            </Col>
          </Row>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12'>SAC Code</Label>
                <input className='form-control' size="md" type='number' {...register("sacCode")} />
              </div>
            </Col>
          </Row>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12'>Professional Fee</Label>
                <input className={`form-control ${errors.professionalFee ? 'is-invalid' : ''}`} size="md" type='number' {...register("professionalFee", { required: "Please Enter Professional Fee" })} />
              </div>
            </Col>
          </Row>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12'>Tax Rate</Label>
                <Select
                  theme={selectThemeColors}
                  className={`react-select col-lg-9 col-sm-12 ${errors.category ? 'is-invalid' : ''}`}
                  classNamePrefix='Select Tax Rate'
                  options={countryOptions}
                  isClearable={false}
                  {...register("taxGroupId")}
                  onChange={data => {
                    console.log(data)
                  }}
                />
              </div>
            </Col>
          </Row>
          <Row className='row-bill-to invoice-spacing'>
            <Col className='my-lg-0 my-1' lg='6' sm='12'>
              <div className='d-lg-flex'>
                <Label className='col-lg-3 col-sm-12'>Description</Label>
                <textarea className={`form-control ${errors.description ? 'is-invalid' : ''}`} size="md" type='text' {...register("description", { required: "Please Enter Professional Fee" })} ></textarea>
              </div>
            </Col>
          </Row>
        </CardBody>
        {/* /Header */}
      </Card>
      <Card>
        <CardBody>
          <div className='modal-footer border-0'>
            <Button color='warning' outline>
              Cancel
            </Button>
            <Button color='primary' type="submit" >
              Save
            </Button>
          </div>
        </CardBody>
      </Card>
    </form>
  )
}

export default AddCard
